import React, { useState } from "react";
import { Link } from "react-router-dom";
import twitterImage from '../../assets/images/twitter.jpeg';
import TwitterIcon from '@mui/icons-material/Twitter';
import { useCreateUserWithEmailAndPassword } from 'react-firebase-hooks/auth';
import { signInWithPopup, GoogleAuthProvider } from 'firebase/auth';
import auth from '../../firebase.init';
import GoogleButton from 'react-google-button';
import './Login.css';

const Signup = () => {

    const [username, setUsername] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');


    const [
        createUserWithEmailAndPassword,
        user,
        loading,
        error,
      ] = useCreateUserWithEmailAndPassword(auth);

      const [signInWithMicrosoft, googleUser, googleLoading, googleError] = useSignInWithGoogle(auth);

      if(user|| googleUser){
        console.log(user)
        console.log(googleUser)
      }

      if(error){
        console.log(error.mesaage)
      }

      if (loading) {
        console.log(loading);
      }


      const handleSubmit = e =>{
        e.preventDefault();
        console.log(email, password);
        createUserWithEmailAndPassword(email, password);
    }
    const handleGoogleSignIn = () => {
        signInWithMicrosoft();
    }



    return (

            <div className='login-container'>
                <div className="image-container">
                    <img className='image' src ={twitterImage} alt="" />
                </div>
                <div className="form-container">
                    <div className='form-box'>
                        <TwitterIcon className='Twittericon' style={{ color: 'skyblue'}}/>
                        <h2 className='heading'>Happening now</h2>
                        <h3 className='heading1'> Join twitter today</h3>

                            <form onSubmit={handleSubmit}>
                                <input 
                                    type="text" 
                                    className='display-name'
                                    placeholder='@username'
                                    onChange={(e) => {setUsername(e.target.value);
                                    }}
                                />
                                <input 
                                    type="text" 
                                    className='display-name'
                                    placeholder='Enter full name'
                                    onChange={(e) => {setName(e.target.value);
                                    }}
                                />


                                <input 
                                    type="email" 
                                    className='email' 
                                    placeholder='Email address' 
                                    onChange={(e) => {
                                        // console.log('Email Value:', e.target.value);
                                        setEmail(e.target.value);
                                    }}
                                />

                                <input 
                                    type="password" 
                                    className='password' 
                                    placeholder='Password'
                                    onChange={(e) => {
                                        // console.log('Password Value:', e.target.value);
                                        setPassword(e.target.value);
                                    }}
                                />

                                <div className='btn-login'>
                                    <button type='submit' className='btn'>Sign Up </button>
                                </div>
                            </form>
                            <hr/>
                            <div className='google-button'>
                                <GoogleButton
                                    className='g-btn'
                                    type='light'
                                    onclick={handleGoogleSignIn}
                                />
                            </div>
                            <div>
                                Already have an account?
                                <Link
                                    to='/login'
                                    style={{
                                        textdecoration:'none',
                                        color:'skyblue',
                                        fontWeight:'600',
                                        marginLeft:'5px'
                                    }}
                                > Login
                                </Link>
                        </div>
                    </div>
                </div>
            </div>
        );
}
export default Signup;
